";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:1;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:14:\"hideContainers\";a:7:{s:4:\"name\";s:14:\"hideContainers\";s:4:\"desc\";s:24:\"mse2_prop_hideContainers\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:14:\"introCutBefore\";a:7:{s:4:\"name\";s:14:\"introCutBefore\";s:4:\"desc\";s:24:\"mse2_prop_introCutBefore\";s:4:\"type\";s:11:\"numberfield\";s:7:\"options\";a:0:{}s:5:\"value\";i:50;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:13:\"introCutAfter\";a:7:{s:4:\"name\";s:13:\"introCutAfter\";s:4:\"desc\";s:23:\"mse2_prop_introCutAfter\";s:4:\"type\";s:11:\"numberfield\";s:7:\"options\";a:0:{}s:5:\"value\";i:250;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:8:\"htagOpen\";a:7:{s:4:\"name\";s:8:\"htagOpen\";s:4:\"desc\";s:18:\"mse2_prop_htagOpen\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:3:\"<b>\";s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:9:\"htagClose\";a:7:{s:4:\"name\";s:9:\"htagClose\";s:4:\"desc\";s:19:\"mse2_prop_htagClose\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:4:\"</b>\";s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:10:\"parentsVar\";a:7:{s:4:\"name\";s:10:\"parentsVar\";s:4:\"desc\";s:20:\"mse2_prop_parentsVar\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:7:\"parents\";s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:8:\"queryVar\";a:7:{s:4:\"name\";s:8:\"queryVar\";s:4:\"desc\";s:18:\"mse2_prop_queryVar\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:5:\"query\";s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:10:\"tplWrapper\";a:7:{s:4:\"name\";s:10:\"tplWrapper\";s:4:\"desc\";s:20:\"mse2_prop_tplWrapper\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:11:\"wrapIfEmpty\";a:7:{s:4:\"name\";s:11:\"wrapIfEmpty\";s:4:\"desc\";s:21:\"mse2_prop_wrapIfEmpty\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:11:\"forceSearch\";a:7:{s:4:\"name\";s:11:\"forceSearch\";s:4:\"desc\";s:21:\"mse2_prop_forceSearch\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:1;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:8:\"minQuery\";a:7:{s:4:\"name\";s:8:\"minQuery\";s:4:\"desc\";s:18:\"mse2_prop_minQuery\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";i:3;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:6:\"fields\";a:7:{s:4:\"name\";s:6:\"fields\";s:4:\"desc\";s:16:\"mse2_prop_fields\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:9:\"onlyIndex\";a:7:{s:4:\"name\";s:9:\"onlyIndex\";s:4:\"desc\";s:19:\"mse2_prop_onlyIndex\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:12:\"onlyAllWords\";a:7:{s:4:\"name\";s:12:\"onlyAllWords\";s:4:\"desc\";s:22:\"mse2_prop_onlyAllWords\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}s:13:\"showSearchLog\";a:7:{s:4:\"name\";s:13:\"showSearchLog\";s:4:\"desc\";s:23:\"mse2_prop_showSearchLog\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:19:\"msearch2:properties\";s:4:\"area\";s:0:\"\";}}"
